draw((0,-4)--(0,5),Arrows);
draw(Circle((5,0),1));
draw(Circle((11,0),2));
dot((5,0));
dot((7,0));
dot((11,0));
label(scale(0.7)*Label("(5,0)",(3,0),S));
label("Q",(7,0),N);
label(scale(0.7)*Label("(11,0)",(11,0),S));
label("$x$",(15,0),S);
label("$y$",(0,5),W);
draw((1,3.46410162)--(14,-4.04145188),Arrows);
[/asy]

